    98	            <h2>{isSignUp ? "Create an account" : "Sign in"}</h2>
    99	            <p className="form-helper">
   100	              {isSignUp
   101	                ? "Enter your email and a strong password to register a new Travelio account."
   102	                : "Use the credentials configured in Google Cloud Identity Platform."}
   103	            </p>
   104	
   105	            <label className="field">
   106	              <span>Email</span>
   107	              <input
   108	                type="email"
   109	                name="username"
   110	                autoComplete="username"
   111	                value={username}
   112	                onChange={(event) => setUsername(event.target.value)}
   113	                placeholder="you@example.com"
   114	                required
   115	                disabled={isAuthenticating}
   116	              />
   117	            </label>
   118	
   119	            <label className="field">
   120	              <span>Password</span>
   121	              <input
   122	                type="password"
   123	                name="password"
   124	                autoComplete={isSignUp ? "new-password" : "current-password"}
   125	                value={password}
   126	                onChange={(event) => setPassword(event.target.value)}
   127	                required
   128	                disabled={isAuthenticating}
   129	              />
   130	            </label>
   131	
   132	            {isSignUp ? (
   133	              <p className="form-note">
   134	                Passwords must meet the policy you configure in Identity Platform.
   135	              </p>
   136	            ) : null}
   137	
   138	            {errorMessage ? (
   139	              <p className="error" role="alert">
   140	                {errorMessage}
   141	              </p>
   142	            ) : null}
   143	
   144	            <button className="primary" type="submit" disabled={isAuthenticating}>
   145	              {isAuthenticating
   146	                ? "Processing..."
   147	                : isSignUp
   148	                  ? "Create account"
   149	                  : "Sign in"}
   150	            </button>
   151	          </form>
   152	
   153	          <p className="form-switch">
   154	            {isSignUp ? "Already have an account?" : "Need a Travelio account?"}
   155	            <button
   156	              type="button"
   157	              className="link-button"
   158	              onClick={toggleMode}
   159	              disabled={isAuthenticating}
   160	            >
   161	              {isSignUp ? "Sign in" : "Create one"}
   162	            </button>
   163	          </p>
   164	        </section>
   165	      )}
   166	
   167	      <footer className="app-footer">
   168	        <p>
   169	          Authentication handled by Google Cloud Identity Platform. Configure your project keys
   170	          in <code>.env</code> files before deploying to GCP.
   171	        </p>
   172	      </footer>
   173	    </div>
   174	  );
   175	}
