import pathlib
md = pathlib.Path('chat-archive/transcript.md').read_text()
html = pathlib.Path('chat-transcript.html').read_text()
start = html.find('<script type="text/plain" id="embedded-md">')
end = html.find('</script>', start)
if start == -1 or end == -1:
    raise SystemExit('embedded-md script not found')
