+    let isActive = true;
+
+    setLoading(true);
+    setError(null);
+
+    loadFirestoreModule()
+      .then(async ({ doc, onSnapshot }) => {
+        if (!isActive) {
+          return;
+        }
+
+        const firestore = await getFirestoreInstance();
+        const itineraryRef = doc(firestore, "itineraries", itineraryId);
+
+        unsubscribe = onSnapshot(
+          itineraryRef,
+          (snapshot) => {
+            if (!snapshot.exists()) {
+              setError("Itinerary not found or may have been deleted.");
+              setItinerary(null);
+              setLoading(false);
+              return;
+            }
+
+            const data = snapshot.data();
+
+            if (data && data.ownerUid !== currentUser.uid) {
+              setError("You do not have access to this itinerary.");
+              setItinerary(null);
+              setLoading(false);
+              return;
+            }
+
+            const travellersData =
+              data && typeof data.travellers === "object"
+                ? (data.travellers as Record<string, unknown>)
+                : {};
+
+            const normalized: Itinerary = {
+              id: snapshot.id,
+              title:
+                typeof data.title === "string" && data.title.trim()
+                  ? data.title
+                  : "Untitled itinerary",
+              createdAt: asTimestamp(data.createdAt),
+              travellers: {
+                males: coerceTravellerCount(travellersData.males),
+                females: coerceTravellerCount(travellersData.females),
+                kids: coerceTravellerCount(travellersData.kids),
+              },
+              startDate:
+                typeof data.startDate === "string"
+                  ? normalizeDateInput(data.startDate) ?? null
+                  : null,
+              endDate:
+                typeof data.endDate === "string"
+                  ? normalizeDateInput(data.endDate) ?? null
+                  : null,
+            };
+
+            setItinerary(normalized);
+            setDraft({
+              title: normalized.title,
+              startDate: normalized.startDate ?? "",
+              endDate: normalized.endDate ?? "",
+              travellers: {
+                males: normalized.travellers.males,
+                females: normalized.travellers.females,
+                kids: normalized.travellers.kids,
+              },
+            });
+            setError(null);
+            setLoading(false);
+          },
+          (snapshotError) => {
+            setError(deriveReadableError(snapshotError));
+            setItinerary(null);
+            setLoading(false);
+          }
+        );
+      })
+      .catch((loadError) => {
+        if (!isActive) {
+          return;
+        }
+
+        setError(deriveReadableError(loadError));
+        setLoading(false);
+      });
+
+    return () => {
+      isActive = false;
+      if (unsubscribe) {
+        unsubscribe();
+      }
+    };
+  }, [currentUser.uid, itineraryId]);
