+            Back to itineraries
+          </button>
+        </div>
+      ) : itinerary ? (
+        <>
+          <header className="builder-header">
+            <div>
+              <h2>{itinerary.title}</h2>
+              <p className="muted">
+                {formatDateRange(itinerary.startDate, itinerary.endDate)} · {totalTravellers}{" "}
+                {travellerLabel}
+              </p>
+            </div>
+            <button className="secondary" type="button" onClick={() => navigate("/")}>
+              Back to itineraries
+            </button>
