+                  onChange={(event) =>
+                    setDraft((prev) => ({
+                      ...prev,
+                      startDate: event.target.value,
+                    }))
+                  }
+                  required
+                  disabled={saving}
+                />
+              </label>
+              <label className="field compact-field">
+                <span>End date</span>
+                <input
+                  type="date"
