{"command":["bash","-lc","python3 - <<'PY'
import json
import subprocess
import sys
from urllib.parse import urlencode
import urllib.request

PROJECT = "cloud-run-day-2025-471903"
BASE_URL = f"https://firestore.googleapis.com/v1/projects/{PROJECT}/databases/(default)/documents"

try:
    TOKEN = subprocess.check_output(["gcloud", "auth", "print-access-token"], text=True).strip()
except subprocess.CalledProcessError as exc:
    sys.stderr.write(exc.stderr)
    sys.exit(1)

HEADERS = {
    "Authorization": f"Bearer {TOKEN}",
    "Content-Type": "application/json",
}


def request_json(method: str, url: str, data: dict | None = None):
    body = None
    if data is not None:
        body = json.dumps(data).encode("utf-8")
    req = urllib.request.Request(url, data=body, method=method, headers=HEADERS)
    try:
        with urllib.request.urlopen(req) as resp:
            return json.loads(resp.read().decode("utf-8") or "{}")
    except urllib.error.HTTPError as exc:
        sys.stderr.write(exc.read().decode("utf-8"))
        raise


def list_collection(path: str):
    documents = []
    page_token = None
    while True:
        params = {"pageSize": 100}
        if page_token:
            params["pageToken"] = page_token
        url = f"{BASE_URL}/{path}?{urlencode(params)}"
        data = request_json("GET", url)
        documents.extend(data.get("documents", []))
        page_token = data.get("nextPageToken")
        if not page_token:
            break
    return documents


def update_owner(path: str, owner_uid: str):
    url = f"{BASE_URL}/{path}?{urlencode({'updateMask.fieldPaths': 'ownerUid'})}"
    payload = {"fields": {"ownerUid": {"stringValue": owner_uid}}}
    request_json("PATCH", url, payload)


itineraries = list_collection("itineraries")
updated = 0
skipped = 0

for itinerary in itineraries:
    name = itinerary.get("name")
    if not name:
        continue
    itinerary_id = name.split("/")[-1]
    owner_field = itinerary.get("fields", {}).get("ownerUid", {})
    owner_uid = owner_field.get("stringValue")
    if not owner_uid:
        skipped += 1
        print(f"Skipping itinerary {itinerary_id}: no ownerUid", file=sys.stderr)
        continue

    events = list_collection(f"itineraries/{itinerary_id}/events")
    for event in events:
        if "ownerUid" in event.get("fields", {}):
            continue
        event_id = event.get("name", "").split("/")[-1]
        if not event_id:
            continue
        path = f"itineraries/{itinerary_id}/events/{event_id}"
        print(f"Updating {path}")
        update_owner(path, owner_uid)
        updated += 1

print(f"Updated {updated} event(s); skipped {skipped} itinerary(ies) without ownerUid.")
PY"],"workdir":"/Users/piyush.mantri/travelio"}

