{"command":["bash","-lc","python3 - <<'PY'
import json
from pathlib import Path
from collections import defaultdict

base = Path('chat-archive')
files = sorted(base.glob('*.jsonl'))

for path in files:
    print(f'=== {path.name} ===')
    users = []
    assistants = []
    commands = []
    file_edits = []
    builds = []
    misc = []
    with path.open() as f:
        for raw in f:
            raw = raw.strip()
            if not raw:
                continue
            data = json.loads(raw)
            typ = data.get('type')
            payload = data.get('payload', {})
            if typ == 'response_item':
                ptype = payload.get('type')
                if ptype == 'message':
                    role = payload.get('role')
                    text_blocks = []
                    for block in payload.get('content', []):
                        text = block.get('text') if isinstance(block, dict) else None
                        if text:
                            text_blocks.append(text.strip())
                    text = '\n'.join(text_blocks)
                    if role == 'user':
                        users.append(text)
                    elif role == 'assistant':
                        assistants.append(text)
                elif ptype == 'function_call':
                    name = payload.get('name')
                    args = payload.get('arguments')
                    commands.append((name, args))
                elif ptype == 'function_call_output':
                    output = payload.get('output') or ''
                    if 'Success. Updated the following files' in output:
                        file_edits.append(output.strip())
                    elif '> ' in output and 'build' in output:
                        builds.append(output.strip())
                    else:
                        misc.append(output.strip())
            elif typ == 'event_msg':
                if payload.get('type') == 'agent_message':
                    assistants.append(payload.get('message', '').strip())
    print('User prompts:')
    for idx, text in enumerate(users, 1):
        print(f'  {idx}. {text}')
    if assistants:
        print('Assistant notes:')
        for idx, text in enumerate(assistants, 1):
            text = text.replace('\n\n', '\n')
            print(f'  {idx}. {text}')
    if commands:
        print('Function calls:')
        for idx, (name, args) in enumerate(commands, 1):
            print(f'  {idx}. {name} {args}')
    if file_edits:
        print('File edits:')
        for entry in file_edits:
            print('  -', entry.split('\n')[0])
    if builds:
        print('Build/test output:')
        for entry in builds:
            print('  -', entry.split('\n')[1].strip())
    if misc:
        print('Other outputs:')
        for entry in misc[:3]:
            snippet = entry.split('\n')[0]
            print('  -', snippet[:120])
        if len(misc) > 3:
            print(f'  ... {len(misc)-3} more outputs omitted')
    print()
PY"],"workdir":"/Users/piyush.mantri/travelio"}

